--CREATE VIEW BASED ON MV_SPECIFICATION-concept, but now build on DBA-VIEWS...
CREATE or replace VIEW sc_lims_dal.dba_v_specification_prop_aggr_json
AS SELECT subsec.part_no
,    subsec.revision
,    sh.status
,    sh.frame_id
,    sh.class3_id
,    sh.issued_date
,    sh.obsolescence_date
,    sk.functionkw
,    jsonb_object_agg(subsec.section_descr, subsec.properties) AS properties
,    sk.keywords
FROM (     SELECT propgrp.part_no
           ,      propgrp.revision
	       ,      propgrp.section_id
	       ,      propgrp.section_descr
           ,      jsonb_object_agg(propgrp.sub_section_descr, propgrp.properties) AS properties
           FROM (  SELECT prop.part_no
		           ,      prop.revision
				   ,      prop.section_id
				   ,      prop.section_descr
				   ,      prop.sub_section_id
				   ,      prop.sub_section_descr
				   ,      jsonb_object_agg(prop.property_group_descr, prop.properties) AS properties
                   FROM ( SELECT sp.part_no
				          ,      sp.revision
						  ,      sp.section_id
						  ,      sp.section_descr
						  ,      sp.sub_section_id
						  ,      sp.sub_section_descr
						  ,      sp.property_group
						  ,      sp.property_group_descr
						  ,      jsonb_object_agg(sp.property_descr, sp.cells - 'Property'::text) AS properties
                          FROM sc_lims_dal.DBA_V_TOT_SPECIFICATION_PROP_FIELD sp
                          GROUP BY sp.part_no, sp.revision, sp.section_id, sp.section_descr, sp.sub_section_id, sp.sub_section_descr, sp.property_group, sp.property_group_descr
						 ) prop
                  GROUP BY prop.part_no, prop.revision, prop.section_id, prop.section_descr, prop.sub_section_id, prop.sub_section_descr 
				) propgrp
          GROUP BY propgrp.part_no, propgrp.revision, propgrp.section_id, propgrp.section_descr
     ) subsec
JOIN sc_interspec_ens.specification_header     sh USING (part_no, revision)
LEFT JOIN sc_lims_dal.mv_specification_keyword sk USING (part_no)
GROUP BY subsec.part_no
, subsec.revision
, sh.status
, sh.frame_id
, sh.class3_id
, sh.issued_date
, sh.obsolescence_date
, sk.functionkw
, sk.keywords
;

