  CREATE OR REPLACE TRIGGER "INTERSPC"."TR_CONDITION_OU" 
   AFTER UPDATE OF description, status
   ON CONDITION
   REFERENCING OLD AS OLD NEW AS NEW
   FOR EACH ROW
DECLARE
   l_revision                    NUMBER;
   l_next_val                    NUMBER;
   lsSource                      iapiType.Source_Type := 'Tr_Condition_Ou';
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorText( iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( lsSource,
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      SELECT revision
        INTO l_revision
        FROM condition_h
       WHERE condition = :NEW.condition
         AND max_rev = 1
         AND lang_id = 1;

      UPDATE CONDITION_h
         SET max_rev = 0
       WHERE CONDITION = :NEW.CONDITION
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM CONDITION_H
       WHERE CONDITION = :NEW.CONDITION;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO CONDITION_H
                  ( CONDITION,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
           VALUES ( :NEW.CONDITION,
                    l_next_val,
                    1,
                    :NEW.description,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1 );

      INSERT INTO CONDITION_H
                  ( CONDITION,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
         SELECT :NEW.condition,
                l_next_val,
                lang_id,
                description,
                last_modified_on,
                last_modified_by,
                1
           FROM condition_h
          WHERE condition = :NEW.condition
            AND revision = l_revision
            AND lang_id <> 1;
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.status <> :NEW.status
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'cd',
                    :NEW.CONDITION,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );
   END IF;
END;
