  CREATE OR REPLACE TRIGGER "INTERSPC"."TR_HEADER_OU" 
   AFTER UPDATE OF description, status
   ON HEADER
   REFERENCING OLD AS OLD NEW AS NEW
   FOR EACH ROW
DECLARE
   l_next_val                    NUMBER;
   l_bubble                      VARCHAR2( 1000 );
   lnRevision                    iapiType.Revision_Type;
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorText( iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( 'TR_HEADER_OU',
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.description <> :NEW.description
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      SELECT revision
        INTO lnRevision
        FROM header_h
       WHERE header_id = :NEW.header_id
         AND max_rev = 1
         AND lang_id = 1;

      UPDATE header_h
         SET max_rev = 0
       WHERE header_id = :NEW.header_id
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM HEADER_H
       WHERE header_id = :NEW.header_id;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO HEADER_H
                  ( header_id,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
           VALUES ( :NEW.header_id,
                    l_next_val,
                    1,
                    :NEW.description,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1 );

      INSERT INTO HEADER_H
                  ( header_id,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
         SELECT :NEW.header_id,
                l_next_val,
                lang_id,
                description,
                last_modified_on,
                last_modified_by,
                1
           FROM header_h
          WHERE header_id = :NEW.header_id
            AND revision = lnRevision
            AND lang_id <> 1;

      SELECT MAX( revision )
        INTO lnRevision
        FROM header_b
       WHERE revision < l_next_val
         AND header_id = :NEW.header_id
         AND lang_id = 1;

      IF lnRevision IS NOT NULL
      THEN
         SELECT bubble
           INTO l_bubble
           FROM header_b
          WHERE revision = lnRevision
            AND lang_id = 1
            AND header_id = :NEW.header_id;

         INSERT INTO header_b
                     ( header_id,
                       revision,
                       lang_id,
                       bubble,
                       last_modified_on,
                       last_modified_by )
              VALUES ( :NEW.header_id,
                       l_next_val,
                       1,
                       l_bubble,
                       SYSDATE,
                       iapiGeneral.SESSION.ApplicationUser.UserId );
      END IF;
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.status <> :NEW.status
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'hd',
                    :NEW.header_id,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );
   END IF;
END;
