   AFTER UPDATE OF description, status, ing_type, recfac, allergen, soi, org_ing, rec_ing
   ON ITING
   REFERENCING OLD AS OLD NEW AS NEW
   FOR EACH ROW
DECLARE
   l_revision                    NUMBER;
   l_next_val                    NUMBER;
   l_allow_ing                   interspc_cfg.parameter_data%TYPE;
   l_allow_ingdet                interspc_cfg.parameter_data%TYPE;
   l_sysdate                     DATE;
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorText( iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( 'TR_ING_OU',
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   SELECT parameter_data
     INTO l_allow_ing
     FROM interspc_cfg
    WHERE parameter = 'allow_ing'
      AND SECTION = 'interspec';

   SELECT parameter_data
     INTO l_allow_ingdet
     FROM interspc_cfg
    WHERE parameter = 'allow_ingdet'
      AND SECTION = 'interspec';

   IF     l_allow_ing = '1'
      AND ( :OLD.description <> :NEW.description )
   THEN
      SELECT revision
        INTO l_revision
        FROM iting_h
       WHERE ingredient = :NEW.ingredient
         AND max_rev = 1
         AND lang_id = 1;

      UPDATE iting_h
         SET max_rev = 0
       WHERE ingredient = :NEW.ingredient
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM ITING_H
       WHERE ingredient = :NEW.ingredient;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO ITING_H
                  ( ingredient,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev,
                    ing_type )
           VALUES ( :NEW.ingredient,
                    l_next_val,
                    1,
                    :NEW.description,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1,
                    :NEW.ing_type );

      INSERT INTO ITING_H
                  ( ingredient,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev,
                    ing_type )
         SELECT :NEW.ingredient,
                l_next_val,
                lang_id,
                description,
                last_modified_on,
                last_modified_by,
                1,
                ing_type
           FROM iting_h
          WHERE ingredient = :NEW.ingredient
            AND revision = l_revision
            AND lang_id <> 1;
   /* If ing type changes, don't make a new revision but just set the modified date */
   ELSIF     l_allow_ing = '1'
         AND :OLD.ing_type <> :NEW.ing_type
   THEN
      UPDATE ITING_H
         SET last_modified_on = SYSDATE,
             last_modified_by = iapiGeneral.SESSION.ApplicationUser.UserId,
             ing_type = :NEW.ing_type
       WHERE ingredient = :NEW.ingredient
         AND max_rev = 1;
   ELSIF     l_allow_ing = '1'
         AND :OLD.status <> :NEW.status
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'in',
                    :NEW.ingredient,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );
   END IF;

   IF     l_allow_ingdet = 1
      AND (    :OLD.recfac <> :NEW.recfac
            OR :OLD.allergen <> :NEW.allergen
            OR :OLD.soi <> :NEW.soi
            OR NVL( :OLD.org_ing,
                    -1 ) <> NVL( :NEW.org_ing,
                                 -1 )
            OR NVL( :OLD.rec_ing,
                    -1 ) <> NVL( :NEW.rec_ing,
                                 -1 ) )
   THEN
      -- One of the ingredient details has been updated
      -- This screen is only opened if l_allow_ingdet = 1
      IF (     :NEW.intl = '1'
           AND iapiGeneral.SESSION.DATABASE.DatabaseType = 'R' )
      THEN
         BEGIN
            SELECT   offset
                   + SYSDATE
              INTO l_sysdate
              FROM itdateoffset;
         EXCEPTION
            WHEN NO_DATA_FOUND
            THEN
               l_sysdate := SYSDATE;
         END;

         UPDATE ITING_H
            SET last_modified_on = l_sysdate
          WHERE ingredient = :NEW.ingredient
            AND max_rev = 1;
      ELSE
         UPDATE ITING_H
            SET last_modified_on = SYSDATE
          WHERE ingredient = :NEW.ingredient
            AND max_rev = 1;
      END IF;

      UPDATE ITING_H
         SET last_modified_by = iapiGeneral.SESSION.ApplicationUser.UserId
       WHERE ingredient = :NEW.ingredient
         AND max_rev = 1;
   END IF;
END;
