   FOR EACH ROW
---------------------------------------------------------------------------
-- $Workfile: TR_INGNOTE_OU.sql $
--     Type:  Trigger creation script
----------------------------------------------------------------------------
--   $Author: evoVaLa3 $
-- $Revision: 6.7.0.0 (06.07.00.00-01.00) $
--  $Modtime: 2014-May-05 12:00 $
--   Project: speCX development
----------------------------------------------------------------------------
--  Abstract:
----------------------------------------------------------------------------
DECLARE
   l_next_val                    NUMBER;
   l_bubble                      VARCHAR2( 1000 );
   l_revision                    NUMBER;
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorTextAndLoginfo( 'TR_INGNOTE_OU',
                                                      '',
                                                      iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( 'TR_INGNOTE_OU',
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND (    :OLD.description <> :NEW.description
            OR (     :OLD.TEXT IS NULL
                 AND :NEW.TEXT IS NOT NULL )
            OR (     :OLD.TEXT IS NOT NULL
                 AND :NEW.TEXT IS NULL )
            OR ( :OLD.TEXT <> :NEW.TEXT ) )
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      SELECT revision
        INTO l_revision
        FROM itingnote_h
       WHERE note_id = :NEW.note_id
         AND lang_id = 1
         AND max_rev = 1;

      UPDATE itingnote_h
         SET max_rev = 0
       WHERE note_id = :NEW.note_id
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM itingnote_H
       WHERE note_id = :NEW.note_id;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO itingnote_H
                  ( note_id,
                    revision,
                    lang_id,
                    description,
                    text,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
           VALUES ( :NEW.note_id,
                    l_next_val,
                    1,
                    :NEW.description,
                    :NEW.TEXT,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1 );

      INSERT INTO itingnote_H
                  ( note_id,
                    revision,
                    lang_id,
                    description,
                    text,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
         SELECT :NEW.note_id,
                l_next_val,
                lang_id,
                description,
                text,
                last_modified_on,
                last_modified_by,
                1
           FROM itingnote_h
          WHERE note_id = :NEW.note_id
            AND revision = l_revision
            AND lang_id <> 1;
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.status <> :NEW.status
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'no',
                    :NEW.note_id,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );

      NULL;
   END IF;
END;
