           VALUES ( :OLD.part_no,
                      SYSDATE
                    +   1
                      / (   24
                          * 60
                          * 60 ),
                    'D',
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :OLD.object_id,
                    :OLD.revision,
