  CREATE OR REPLACE TRIGGER "INTERSPC"."TR_PROP_OU" 
   AFTER UPDATE OF description, status
   ON PROPERTY
   REFERENCING OLD AS OLD NEW AS NEW
   FOR EACH ROW
---------------------------------------------------------------------------
-- $Workfile: TR_PROP_OU.sql $
--     Type:  Trigger creation script
----------------------------------------------------------------------------
--   $Author: evoVaLa3 $
-- $Revision: 6.7.0.0 (06.07.00.00-01.00) $
--  $Modtime: 2014-May-05 12:00 $
--   Project: speCX development
----------------------------------------------------------------------------
--  Abstract:
----------------------------------------------------------------------------
DECLARE
   l_next_val                    NUMBER;
   l_bubble                      VARCHAR2( 1000 );
   l_revision                    NUMBER;
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorTextAndLoginfo( 'TR_PROP_OU',
                                                      '',
                                                      iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( 'TR_PROP_OU',
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.description <> :NEW.description
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      SELECT revision
        INTO l_revision
        FROM property_h
       WHERE property = :NEW.property
         AND lang_id = 1
         AND max_rev = 1;

      UPDATE property_h
         SET max_rev = 0
       WHERE property = :NEW.property
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM PROPERTY_H
       WHERE property = :NEW.property;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO PROPERTY_H
                  ( property,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
           VALUES ( :NEW.property,
                    l_next_val,
                    1,
                    :NEW.description,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1 );

      INSERT INTO PROPERTY_H
                  ( property,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
         SELECT :NEW.property,
                l_next_val,
                lang_id,
                description,
                last_modified_on,
                last_modified_by,
                1
           FROM property_h
          WHERE property = :NEW.property
            AND revision = l_revision
            AND lang_id <> 1;

      SELECT MAX( revision )
        INTO l_revision
        FROM property_b
       WHERE revision < l_next_val
         AND property = :NEW.property
         AND lang_id = 1;

      IF l_revision IS NOT NULL
      THEN
         SELECT bubble
           INTO l_bubble
           FROM property_b
          WHERE revision = l_revision
            AND lang_id = 1
            AND property = :NEW.property;

         INSERT INTO property_b
                     ( property,
                       revision,
                       lang_id,
                       bubble,
                       last_modified_on,
                       last_modified_by )
              VALUES ( :NEW.property,
                       l_next_val,
                       1,
                       l_bubble,
                       SYSDATE,
                       iapiGeneral.SESSION.ApplicationUser.UserId );
      END IF;
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.status <> :NEW.status
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'sp',
                    :NEW.property,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );

      NULL;
   END IF;
END;
