  CREATE OR REPLACE TRIGGER "INTERSPC"."TR_SECTION_OU" 
   AFTER UPDATE OF description, status
   ON SECTION
   REFERENCING OLD AS OLD NEW AS NEW
   FOR EACH ROW
DECLARE
   l_next_val                    NUMBER;
   l_bubble                      VARCHAR2( 1000 );
   l_revision                    NUMBER;
   lsSource                      iapiType.Source_Type := 'Tr_Section_Ou';
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorText( iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( lsSource,
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.description <> :NEW.description
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      SELECT revision
        INTO l_revision
        FROM section_h
       WHERE section_id = :NEW.section_id
         AND max_rev = 1
         AND lang_id = 1;

      UPDATE section_h
         SET max_rev = 0
       WHERE section_id = :NEW.section_id
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM section_h
       WHERE section_id = :NEW.section_id;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO SECTION_H
                  ( section_id,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
           VALUES ( :NEW.section_id,
                    l_next_val,
                    1,
                    :NEW.description,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1 );

      INSERT INTO SECTION_H
                  ( section_id,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
         SELECT :NEW.section_id,
                l_next_val,
                lang_id,
                description,
                last_modified_on,
                last_modified_by,
                1
           FROM section_h
          WHERE section_id = :NEW.section_id
            AND revision = l_revision
            AND lang_id <> 1;

      SELECT MAX( revision )
        INTO l_revision
        FROM section_b
       WHERE revision < l_next_val
         AND section_id = :NEW.section_id
         AND lang_id = 1;

      IF l_revision IS NOT NULL
      THEN
         SELECT bubble
           INTO l_bubble
           FROM section_b
          WHERE revision = l_revision
            AND lang_id = 1
            AND section_id = :NEW.section_id;

         INSERT INTO section_b
                     ( section_id,
                       revision,
                       lang_id,
                       bubble,
                       last_modified_on,
                       last_modified_by )
              VALUES ( :NEW.section_id,
                       l_next_val,
                       1,
                       l_bubble,
                       SYSDATE,
                       iapiGeneral.SESSION.ApplicationUser.UserId );
      END IF;
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.status <> :NEW.status
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'sc',
                    :NEW.section_id,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );
   END IF;
END;
