  CREATE OR REPLACE TRIGGER "INTERSPC"."TR_UOM_GROUP_OU" 
   AFTER UPDATE OF description, status
   ON UOM_GROUP
   REFERENCING OLD AS OLD NEW AS NEW
   FOR EACH ROW
DECLARE
   l_next_val                    NUMBER;
   l_bubble                      VARCHAR2( 1000 );
   lnRevision                    NUMBER;
   lnRetVal                      iapiType.ErrorNum_Type;
BEGIN
   IF iapiGeneral.SESSION.ApplicationUser.UserId IS NULL
   THEN
      lnRetVal := iapiGeneral.SetErrorText( iapiConstantDbError.DBERR_NOINITSESSION );
      iapiGeneral.LogError( 'TR_UOM_GROUP_OU',
                            '',
                            iapiGeneral.GetLastErrorText( ) );
      RAISE_APPLICATION_ERROR( -20000,
                               iapiGeneral.GetLastErrorText( ) );
   END IF;

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.description <> :NEW.description
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      SELECT revision
        INTO lnRevision
        FROM UOM_GROUP_H
       WHERE uom_group = :NEW.uom_group
         AND max_rev = 1
         AND lang_id = 1;

      UPDATE UOM_GROUP_H
         SET max_rev = 0
       WHERE uom_group = :NEW.uom_group
         AND max_rev = 1;

      SELECT (    (   TRUNC(   MAX( revision )
                             / 100 )
                    + 1 )
               * 100 )
        INTO l_next_val
        FROM UOM_GROUP_H
       WHERE uom_group = :NEW.uom_group;

      IF l_next_val IS NULL
      THEN
         l_next_val := 1;
      END IF;

      INSERT INTO UOM_GROUP_H
                  ( uom_group,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
           VALUES ( :NEW.uom_group,
                    l_next_val,
                    1,
                    :NEW.description,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    1 );

      INSERT INTO UOM_GROUP_H
                  ( uom_group,
                    revision,
                    lang_id,
                    description,
                    last_modified_on,
                    last_modified_by,
                    max_rev )
         SELECT :NEW.uom_group,
                l_next_val,
                lang_id,
                description,
                last_modified_on,
                last_modified_by,
                1
           FROM UOM_GROUP_H
          WHERE uom_group = :NEW.uom_group
            AND revision = lnRevision
            AND lang_id <> 1;

      SELECT MAX( revision )
        INTO lnRevision
        FROM UOM_GROUP_B
       WHERE revision < l_next_val
         AND uom_group = :NEW.uom_group
         AND lang_id = 1;

      IF lnRevision IS NOT NULL
      THEN
         SELECT bubble
           INTO l_bubble
           FROM UOM_GROUP_B
          WHERE revision = lnRevision
            AND lang_id = 1
            AND uom_group = :NEW.uom_group;

         INSERT INTO UOM_GROUP_B
                     ( uom_group,
                       revision,
                       lang_id,
                       bubble,
                       last_modified_on,
                       last_modified_by )
              VALUES ( :NEW.uom_group,
                       l_next_val,
                       1,
                       l_bubble,
                       SYSDATE,
                       iapiGeneral.SESSION.ApplicationUser.UserId );
      END IF;
   END IF;

   --IF UOM_GROUP TABLE PLAY IN 3-TIER THEN USE THE ALLOW

   IF     (     (     iapiGeneral.SESSION.DATABASE.DatabaseType IN( 'L', 'R' )
                  AND :NEW.intl = '0' )
            OR ( iapiGeneral.SESSION.DATABASE.DatabaseType = 'G' ) )
      AND :OLD.status <> :NEW.status
      AND iapiGeneral.SESSION.DATABASE.Configuration.Glossary
   THEN
      INSERT INTO itensslog
                  ( en_tp,
                    en_id,
                    status_change_date,
                    user_id,
                    status )
           VALUES ( 'ug',
                    :NEW.uom_group,
                    SYSDATE,
                    iapiGeneral.SESSION.ApplicationUser.UserId,
                    :NEW.status );
   END IF;

END;
